    /*** 利用abi信息 与 bin信息 生成对应的abi,bin文件* @param abi 合约编译后的abi信息* @param bin 合约编译后的bin信息*/
    public static void generateABIAndBIN(String abi, String bin, String abiFileName, String binFileName) {
        File abiFile = new File("/Users/zenghuikang/crud-generator/src/back/8096/" + abiFileName);
        File binFile = new File("/Users/zenghuikang/crud-generator/src/back/8096/" + binFileName);
        BufferedOutputStream abiBos = null;
        BufferedOutputStream binBos = null;
        try {
            FileOutputStream abiFos = new FileOutputStream(abiFile);
            FileOutputStream binFos = new FileOutputStream(binFile);
            abiBos = new BufferedOutputStream(abiFos);
            binBos = new BufferedOutputStream(binFos);
            abiBos.write(abi.getBytes());
            abiBos.flush();
            binBos.write(bin.getBytes());
            binBos.flush();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (abiBos != null) {
                try {
                    abiBos.close();
                    ;
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            if (binBos != null) {
                try {
                    binBos.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

